package com.practice.util;

import javax.xml.bind.DatatypeConverter;

public class ByteArrayUtil {
    static int  currentIndex =0;
    public static void main(String[] args) {
        String hexEncoded = "";
        byte[] bytes = DatatypeConverter.parseHexBinary(hexEncoded);

        System.out.println(getStringFromBytes(getBytes(2,bytes)));
        System.out.println(getStringFromBytes(getBytes(3, bytes)));

    }

    public static byte[] getBytes(int numOfBytes,byte[] hexBytesArr) {
        byte[] byteArr = new byte[numOfBytes];

        for (int i = 0; i < numOfBytes; i++) {
            if (currentIndex >= hexBytesArr.length) {
                break;
            }
            byteArr[i] = hexBytesArr[currentIndex++];
        }

        return byteArr;
    }

    private static String getStringFromBytes(byte[] bs) {
        return new String(bs);
    }
}
